  it('should spread extra props to input', () => {
    const wrapper = mountWithProvider(
      <InputGroup
        id={id}
        label={label}
        placeholder={placeholder}
        inputValue={inputValue}
        onChange={onChange}
        autoComplete={autoComplete}
